using DES.Realization.Lib.Helper;
using System;
using System.Text;
using Xunit;

namespace DES.Realization.Tests.ConverterTests;
public class BinaryToStringTests
{
    [Theory]
    [InlineData("my text", "01101101011110010010000001110100011001010111100001110100")]
    [InlineData("daria daria", "0110010001100001011100100110100101100001001000000110010001100001011100100110100101100001")]
    [InlineData("русскую раскладку тоже нужно тестить!!", "")]
    [InlineData("$пецCimв0лы нужно пр0веPить", "")]
    public void BinaryToStringUTF8(string expected, string input)
    {
        var actual = Converter.BinaryToString(input, Encoding.UTF8);
        Assert.Equal(expected, actual);
    }

}
